import React from "react";

const Hero = () => {
  const scrollToContact = () => {
    const contactSection = document.getElementById("contact");
    if (contactSection) {
      contactSection.scrollIntoView({ behavior: "smooth" });
    }
  };

  return (
    <section id="hero" className="hero">
      <div className="hero-content">
        <h1 className="hero-title">محمد بن عفيف</h1>
        <p className="hero-description">للمحاماة والاستشارات القانونية.</p>
        <button onClick={scrollToContact} className="hero-btn" style={{ 
          all: "unset",  // Resets button to behave like a link
          cursor: "pointer", 
          display: "inline-block",
          textDecoration: "none", // Ensures no underline
          padding: "10px 20px", 
          background: "#007bff",  // Same as your original button
          color: "#fff",
          borderRadius: "5px",
          fontSize: "16px",
          textAlign: "center"
        }}>
          اطلب استشارة فورية
        </button>
      </div>
    </section>
  );
};

export default Hero;
