import './App.css';
import React, { useEffect, useState } from "react";
import { BrowserRouter as Router } from "react-router-dom";
import RoutesConfig from "./routes/routes"; // Your React Router Config

function App() {
  const [data, setData] = useState(null);

  // Fetch data from the backend API
  useEffect(() => {
    fetch('http://localhost:5000/api/data') // Backend endpoint
      .then(response => response.json())
      .then(data => setData(data.message)) // Assuming backend sends 'message'
      .catch(err => console.error('Error fetching data:', err));
  }, []);

  return (
    <Router>
      <div className="App">
        <h1>Welcome to Lawyer Website</h1>
        {/* Display the fetched data from the backend */}


        {/* Frontend Routes */}
        <RoutesConfig />
      </div>
    </Router>
  );
}

export default App;
