import React, { useRef } from "react";
import { FaChevronLeft, FaChevronRight, FaRegAddressCard, FaTasks, FaClipboardCheck, FaGavel, FaBuilding, FaFileSignature, FaBalanceScale, FaHandshake, FaFileInvoice, FaComments } from 'react-icons/fa';

const Services = () => {
  const servicesRef = useRef(null);
  const followupRef = useRef(null);

  const scrollLeft = (ref) => {
    if (ref.current) {
      ref.current.scrollBy({ left: -300, behavior: "smooth" });
    }
  };

  const scrollRight = (ref) => {
    if (ref.current) {
      ref.current.scrollBy({ left: 300, behavior: "smooth" });
    }
  };

  return (
    <>
      {/* قسم خدماتنا القانونية */}
      <section id="services">
        <div className="services-header">
          <h2>خدماتنا القانونية</h2>
        </div>
        <div className="slider-container">
          <button className="slider-btn left" onClick={() => scrollLeft(servicesRef)}>
            <FaChevronLeft />
          </button>
          <div className="services-grid" ref={servicesRef}>
            <div className="service-box"><FaGavel className="service-icon" /><h3>التحكيم</h3><p>توفير الوسائل البديلة لحل المنازعات مثل التحكيم والتسويات الودية.</p></div>
            <div className="service-box"><FaBuilding className="service-icon" /><h3>قطاع الأعمال</h3><p>خدمات تأسيس الشركات، إعداد اللوائح، وتسجيل العلامات التجارية.</p></div>
            <div className="service-box"><FaFileSignature className="service-icon" /><h3>العقود</h3><p>إعداد ومراجعة العقود لضمان الحقوق وتحقيق الشفافية.</p></div>
            <div className="service-box"><FaBalanceScale className="service-icon" /><h3>الاستشارات القانونية</h3><p>تقديم استشارات قانونية متخصصة.</p></div>
            <div className="service-box"><FaHandshake className="service-icon" /><h3>التقاضي والدفاع</h3><p>تمثيل العملاء في المحاكم والدفاع عن حقوقهم.</p></div>
          </div>
          <button className="slider-btn right" onClick={() => scrollRight(servicesRef)}>
            <FaChevronRight />
          </button>
        </div>
      </section>

      {/* قسم المتابعة مع العميل */}
      <section id="client-followup">
        <div className="followup-header">
          <h2>المتابعة مع العميل</h2>
        </div>
        <div className="slider-container">
          <button className="slider-btn left" onClick={() => scrollLeft(followupRef)}>
            <FaChevronLeft />
          </button>
          <div className="followup-steps" ref={followupRef}>
            <div className="step-box"><FaRegAddressCard className="step-icon" /><h3>استقبال الطلب</h3><p>جمع معلومات العميل.</p></div>
            <div className="step-box"><FaTasks className="step-icon" /><h3>تحليل الطلب</h3><p>دراسة القضية ووضع خطة العمل.</p></div>
            <div className="step-box"><FaClipboardCheck className="step-icon" /><h3>التنفيذ</h3><p>تنفيذ الإجراءات القانونية.</p></div>
            <div className="step-box"><FaFileInvoice className="step-icon" /><h3>إغلاق القضية</h3><p>استكمال الإجراءات وإصدار التقارير.</p></div>
            <div className="step-box"><FaComments className="step-icon" /><h3>التقييم</h3><p>الحصول على آراء العملاء.</p></div>
          </div>
          <button className="slider-btn right" onClick={() => scrollRight(followupRef)}>
            <FaChevronRight />
          </button>
        </div>
      </section>
    </>
  );
};

export default Services;
